INSERT INTO `activities` VALUES (181,'Apolitical Digital 101 Video: Trust, diversity and culture: how to create an effective government workplace','https://apolitical.co/en/solution_article/servant-leadership-how-to-build-a-workplace-thats-ready-to-serve','This video provides an overview of Servant Leadership, and why it has the power to make transformation stick. Servant leadership is all about focusing on the needs of people, not the organization, first. Servant leaders clear a path and empower their staff, placing the needs of the team ahead of their own desires. Taking the stance that \"leadership is action, not position\" - Donald McGannon','n/a','',NULL,2,NULL,'',0,0,NULL,NULL,0,1,NULL,1,'2020-05-09 19:59:19',1,'2020-09-09 20:06:58',1,1,'10 mins or less',NULL);
