INSERT INTO `activities` VALUES (206,'Harvard Implicit Assumptions Test','https://www.glaxdiversitycouncil.com/resources/harvard-implicit-association-test-iat/','Here is a tool that allows each of us to discover hidden cognitive biases.  Most people are aware of their own overt biases, but it is very difficult for us to become aware of our covert biases.  This is a test that can be taken by each individual for their own benefit. <p> Please note by completing the assessment you are consenting to your personal information being stored in the US. (User test results are anonymized and added to data sets to contribute to on-going research.)','','',NULL,2,NULL,'',0,0,NULL,NULL,0,0,NULL,1,'2020-05-11 01:26:12',1,'2020-05-11 01:26:12',1,4,'10 mins or less',NULL);
