INSERT INTO `activities` VALUES (277,'New Relationship with Indigenous People','https://www2.gov.bc.ca/gov/content/governments/indigenous-people/new-relationship','As part of its work to create true and lasting reconciliation with Indigenous peoples, the government of British Columbia is committed to adopting and implementing the United Nations Declaration on the Rights of Indigenous Peoples (UN declaration), and the Truth and Reconciliation Commission of Canadas Calls to Action (calls to action).<p><p> This website provides important information and links to additional information regarding building relationships with Indigenous people.','','','',2,NULL,NULL,0,0,NULL,NULL,0,0,NULL,1,'2020-06-26 22:29:07',1,'2020-06-26 22:29:07',1,2,'1 hour or more',NULL);
