INSERT INTO `activities` VALUES (279,'Reflection on Corporate Priorities','','After exploring and learning about corporate priorities choose three to reflect on and answer the questions below. <p><p> 1) Think about how you would explain these corporate priorities to someone who didnt know about them. 2) Why are these three corporate priorities important? <p><p> 3) How do these corporate priorities connect to your work? <p><p> 4) Do you need to learn more about these corporate priorities to effectively do your work? How will you do this?','','','',2,NULL,NULL,0,0,NULL,NULL,0,0,NULL,1,'2020-06-26 22:38:01',1,'2020-06-26 22:41:10',1,4,'',NULL);
