INSERT INTO `activities` VALUES (425,'Next Steps - SBCPS - Coaching','','<p>Now that youve completed your pre-work, please follow these simple steps to help you join and successfully participate in the live session:</p><ol><li>Make sure you read the email we have sent you for instructions on how to join the live web session.</li><li>Print or make sure you have your <b>practice instructions</b> and your <b>sample situations</b> handy during the session.  It will serve as a helpful resource during your practice.</li></ol><p><b>Note: There is no link for this item.</b></p>','','','',2,NULL,NULL,0,0,NULL,NULL,0,0,NULL,1,'2021-02-10 22:34:19',1,'2021-02-10 22:44:35',1,2,'Under 10 mins','next-steps-sbcps-coaching');
