INSERT INTO `steps` VALUES (75,'Step 8','step-8','Explain 3 corporate government priorities and why they matter. Articulate what you need to know.','',0,'2020-05-11 18:51:16',1,'2021-02-21 22:46:37',1);
INSERT INTO `steps` VALUES (80,'Step 1 - Principles','step-1-principles','We prefer open source projects. All code that we develop is open source, and we lean towards open source tools to create it. Learn what this means and how it applies.',NULL,0,'2020-07-14 17:12:49',1,'2021-02-21 22:46:37',1);
